mapper.insert:
insert
  into c_member
  	 (
  	   email
  	 , user_pwd
  	 , user_name
  	 , gender
  	 , brithday
  	 , phone
  	 , address
  	 ,invite_code
  	 )
values
     (
       #{email}
     , #{userPwd}
     , #{userName}
     , #{gender}
     , #{brithday}
     , #{phone}
     , #{address}   
     , #{inviteCode}
     )

mapper.select:
select email
  from c_member
 where user_name = #{userName}
   and phone = #{phone}
   and status != 'N'

select email
  from c_member
 where user_name = #{userName}
   and email = #{email}
   and status != 'N'

mapper.update:
update c_member
	set user_pwd = #{userPwd}
  where email = #{email}
	and status != 'N'
